using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace AdventOfCode2016
{
    class Day2
    {
        public static string Part1()
        {

            //string instructions = "ULL\nRRDDD\nLURDL\nUUUUD";

            string instructions = @"



";

            var decode = new Dictionary<char, int> { { 'U', -3 }, { 'D', 3 }, { 'L', -1 }, { 'R', 1 } };
            var canmove = new Dictionary<char, Func<int, bool>> { { 'U', (x) => (x > 3) }, { 'D', (x) => (x < 7) }, { 'L', (x) => ((x -1) % 3) > 0 }, { 'R', (x) => ((x - 1) % 3) < 2 } };

            int current = 5;
            string combination = "";

            foreach (char c in instructions)
            {
                if (c == '\n')
                {
                    combination += current.ToString();
                }
                else if (c != '\r')
                {
                    if (canmove[c](current))
                    {
                        current += decode[c];
                    }
                }
            }


            return combination + current.ToString();
        }

        public static string Part2()
        {

            //string instructions = "ULL\nRRDDD\nLURDL\nUUUUD";

            string keypad = "xx1xxx234x56789xABCxxxDxx";

            string instructions = @"



";

            var decode = new Dictionary<char, int> { { 'U', -5 }, { 'D', 5 }, { 'L', -1 }, { 'R', 1 } };
            var canmove = new Dictionary<char, Func<int, bool>> { { 'U', (x) => (x > 4) }, { 'D', (x) => (x < 20) }, { 'L', (x) => (x % 5) > 0 }, { 'R', (x) => (x % 5) < 4 } };

            int current = 10;
            string combination = "";

            foreach (char c in instructions)
            {
                if (c == '\n')
                {
                    combination += keypad[current];
                }
                else if (c != '\r')
                {
                    if (canmove[c](current))
                    {
                        var next = current + decode[c];
                        if (keypad[next] != 'x')
                        {
                            current += decode[c];
                        }
                    }
                }
            }


            return combination + keypad[current];
        }
    }
}
